function handler(event) {
        var request = event.request;
        var querystring = request.querystring;
        if (!querystring['number']){
          var newUri;
          var randomkey = getRndInteger(1, 28);
          newUri = '/?number=' + randomkey;
          var response = {
            statusCode: 302,
            statusDescription: 'Found',
            headers: {
              location: { value: newUri },
            },
          };
          return response;
        }
        return request;
      }

      function getRndInteger(min, max) {
        min = Math.ceil(min); // rounds a number up to the next largest integer
        max = Math.floor(max); // returns the largest integer less than or equal to a given number
        return Math.floor(Math.random() * (max - min + 1) + min); //The maximum is inclusive and the minimum is inclusive
      }

